function SM  = sm_assemble_global(mesh_size,pol_deg,SM_local)    % This function is where we want to build the global stiffness matrix. For our approach that means that we have to    % compute the local stiffness matrix first and then build the global stiffness matrix via an indices renumbering like we     % saw in the lecture. For more information see the documentation.        % Initialize properties needed for renumbering the shape function and for the loops.    cells_per_row= (1/mesh_size);    number_of_cells = cells_per_row^2;    number_of_nodes = ((pol_deg * cells_per_row)+1)^2;    SM = zeros(number_of_nodes,number_of_nodes);    Nodes = mesh_nodes(mesh_size,pol_deg);        % Build SM out of the entries of the local stiffness matrix via appropriate renumbering.    for k=1:number_of_cells        SM(int32(Nodes(k,:)),int32(Nodes(k,:)))+=SM_local;    endfor    endfunction